import { useState } from 'react'

const Tasks = () =>
{
  const [tasks, setTasks] = useState([
    {
      id: 0,
      text: "Meeting 1",
      day: "Oct 4th at 16:00",
      reminder: true
    },
    {
      id: 1,
      text: "Get Dog Food",
      day: "Oct 30th",
      reminder: true
    },
    {
      id: 2,
      text: "Pickup computer from mom",
      day: "Oct 4th at 17:00",
      reminder: true
    },
    {
      id: 3,
      text: "Get harddrive",
      day: "Oct 5th",
      reminder: true
    }
  ])
  return (
    <>
      {
        tasks.map((task) =>
        (
          <h3 key={task.id}>{task.text}</h3>
        )
        )
      }
    </>)
}

export default Tasks
